import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { useTheme } from '../hooks/useTheme';

const PatientCard = ({ patient, onPress }) => {
  const { theme } = useTheme();

  return (
    <TouchableOpacity
      style={[styles.container, { backgroundColor: theme.colors.card }]}
      onPress={onPress}
    >
      <Text style={[styles.name, { color: theme.colors.text }]}>{patient.name}</Text>
      <Text style={[styles.info, { color: theme.colors.text }]}>Idade: {patient.age}</Text>
      <Text style={[styles.info, { color: theme.colors.text }]}>Diagn√≥stico: {patient.diagnosis}</Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 15,
    borderRadius: 10,
    marginBottom: 10,
  },
  name: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  info: {
    fontSize: 14,
  },
});

export default PatientCard;
